(function($){$.fn.limitText=function(t){this.each(function(){var e={characterLimit:240,readLessText:"Read less",readMoreText:"Read more",showEllipsis:!0},s=$.extend({},e,t),i=$(this),a=i.html(),l=i.text();if(l.length>s.characterLimit){for(var h=l.split(/\s+/),r="";(r+h[0]+" ").length<s.characterLimit;)r+=h.shift()+" ";s.showEllipsis&&(r+="... "),$(this).html("");var n=$("<div/>",{"class":"text-short",text:r}),c=$("<a/>",{"class":"toggle",text:s.readMoreText});n.append(c),i.append(n);var o=$("<div/>",{"class":"text-full",html:a+" "}),d=$("<a/>",{"class":"toggle",text:s.readLessText});o.append(d),i.append(o)}}),$(".text-short").show(),$(".text-full").hide(),$(".toggle").click(function(){$(this).parent().hide(),$(this).parent().siblings().show()})};}(jQuery));